import React, { useRef, useMemo } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { Points, PointMaterial } from '@react-three/drei';
import * as THREE from 'three';

// Custom Perlin Noise implementation
class PerlinNoise {
  constructor(seed = Math.random()) {
    this.permutation = new Array(256).fill(0).map((_, i) => i);
    for (let i = 255; i > 0; i--) {
      const j = Math.floor(seed * (i + 1));
      [this.permutation[i], this.permutation[j]] = [this.permutation[j], this.permutation[i]];
    }
    this.p = [...this.permutation, ...this.permutation];
  }

  fade(t) {
    return t * t * t * (t * (t * 6 - 15) + 10);
  }

  lerp(t, a, b) {
    return a + t * (b - a);
  }

  grad(hash, x) {
    return (hash & 1) === 0 ? x : -x;
  }

  perlin2(x, y) {
    const X = Math.floor(x) & 255;
    const Y = Math.floor(y) & 255;
    x -= Math.floor(x);
    y -= Math.floor(y);
    const u = this.fade(x);
    const v = this.fade(y);
    const A = this.p[X] + Y;
    const AA = this.p[A];
    const AB = this.p[A + 1];
    const B = this.p[X + 1] + Y;
    const BA = this.p[B];
    const BB = this.p[B + 1];
    return this.lerp(v, this.lerp(u, this.grad(this.p[AA], x), this.grad(this.p[BA], x - 1)),
      this.lerp(u, this.grad(this.p[AB], x), this.grad(this.p[BB], x - 1)));
  }
}

function FluidParticles({ count = 2000 }) {
  const mesh = useRef();
  const noise = useMemo(() => new PerlinNoise(Math.random()), []);
  
  const positions = useMemo(() => {
    const positions = new Float32Array(count * 3);
    for (let i = 0; i < count; i++) {
      positions[i * 3] = (Math.random() - 0.5) * 20; // x
      positions[i * 3 + 1] = (Math.random() - 0.5) * 20; // y
      positions[i * 3 + 2] = (Math.random() - 0.5) * 20; // z
    }
    return positions;
  }, [count]);

  const velocities = useMemo(() => {
    const velocities = [];
    for (let i = 0; i < count; i++) {
      velocities.push({ x: 0, y: 0, z: 0 });
    }
    return velocities;
  }, [count]);

  useFrame((state) => {
    const time = state.clock.getElapsedTime();
    const positions = mesh.current.geometry.attributes.position.array;
    const scale = 0.1;
    const speed = 0.03;
    
    for (let i = 0; i < count; i++) {
      const ix = i * 3;
      const iy = i * 3 + 1;
      const iz = i * 3 + 2;
      
      let x = positions[ix];
      let y = positions[iy];
      let z = positions[iz];

      // Flow direction from Perlin noise (2D flow field)
      let angle = noise.perlin2(x * scale, y * scale) * Math.PI * 2;
      let vx = Math.cos(angle) * speed;
      let vy = Math.sin(angle) * speed;

      // Add time-based variation for more dynamic flow
      let timeAngle = noise.perlin2(x * scale + time * 0.1, y * scale + time * 0.1) * Math.PI * 2;
      vx += Math.cos(timeAngle) * speed * 0.5;
      vy += Math.sin(timeAngle) * speed * 0.5;

      // Update positions
      x += vx;
      y += vy;
      z += Math.sin(time * 0.5 + x * 0.1) * 0.01; // Subtle z-motion

      // Wrap around edges
      if (x > 10) x = -10;
      if (x < -10) x = 10;
      if (y > 10) y = -10;
      if (y < -10) y = 10;
      if (z > 10) z = -10;
      if (z < -10) z = 10;

      positions[ix] = x;
      positions[iy] = y;
      positions[iz] = z;
    }
    
    mesh.current.geometry.attributes.position.needsUpdate = true;
  });

  return (
    <Points ref={mesh}>
      <bufferGeometry>
        <bufferAttribute
          attach="attributes-position"
          count={count}
          array={positions}
          itemSize={3}
        />
      </bufferGeometry>
      <PointMaterial
        transparent
        color="#00ffff"
        size={0.07}
        sizeAttenuation={true}
        depthWrite={false}
        opacity={0.7}
        blending={THREE.AdditiveBlending}
      />
    </Points>
  );
}

function VortexField() {
  const mesh = useRef();
  
  useFrame((state) => {
    const time = state.clock.getElapsedTime();
    mesh.current.rotation.x = Math.sin(time * 0.1) * 0.1;
    mesh.current.rotation.y = Math.cos(time * 0.1) * 0.1;
  });

  return (
    <mesh ref={mesh} position={[0, 0, -5]}>
      <torusGeometry args={[8, 2, 16, 100]} />
      <meshBasicMaterial 
        color="#0080ff" 
        transparent 
        opacity={0.1} 
        wireframe 
      />
    </mesh>
  );
}

function PressureGradient() {
  const mesh = useRef();
  
  useFrame((state) => {
    const time = state.clock.getElapsedTime();
    mesh.current.rotation.z = time * 0.05;
    mesh.current.material.opacity = 0.05 + Math.sin(time * 0.5) * 0.02;
  });

  return (
    <mesh ref={mesh} position={[0, 0, -3]}>
      <sphereGeometry args={[12, 32, 32]} />
      <meshBasicMaterial 
        color="#00aaff" 
        transparent 
        opacity={0.05} 
        wireframe 
      />
    </mesh>
  );
}

function FlowLines() {
  const mesh = useRef();
  
  useFrame((state) => {
    const time = state.clock.getElapsedTime();
    mesh.current.rotation.y = time * 0.02;
  });

  return (
    <group ref={mesh}>
      {[...Array(6)].map((_, i) => (
        <mesh key={i} position={[0, 0, -2]} rotation={[0, (i * Math.PI) / 3, 0]}>
          <cylinderGeometry args={[0.01, 0.01, 12, 8]} />
          <meshBasicMaterial 
            color="#00ffff" 
            transparent 
            opacity={0.1} 
            wireframe 
          />
        </mesh>
      ))}
    </group>
  );
}

function FluidBackground() {
  return (
    <div className="fluid-background">
      <Canvas
        camera={{ position: [0, 0, 10], fov: 75 }}
        style={{ position: 'fixed', top: 0, left: 0, zIndex: -1 }}
      >
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} intensity={1} />
        <FluidParticles count={2000} />
        <VortexField />
        <PressureGradient />
        <FlowLines />
      </Canvas>
    </div>
  );
}

export default FluidBackground;
