import React from 'react';
import { motion } from 'framer-motion';
import { GraduationCap, Building, Calendar, MapPin, Award } from 'lucide-react';
import './Education.css';

const Education = () => {
  const education = [
    {
      degree: 'Ph.D. in Computer Science',
      institution: 'Stanford University',
      location: 'Stanford, CA',
      period: '2015 - 2019',
      gpa: '4.0/4.0',
      thesis: 'Advanced Machine Learning Algorithms for Predictive Analytics',
      highlights: [
        'Graduated with distinction',
        'Recipient of Outstanding Dissertation Award',
        'Published 8 peer-reviewed papers during PhD',
        'Teaching Assistant for 6 courses'
      ]
    },
    {
      degree: 'M.S. in Computer Science',
      institution: 'MIT',
      location: 'Cambridge, MA',
      period: '2013 - 2015',
      gpa: '3.9/4.0',
      thesis: 'Machine Learning Applications in Data Mining',
      highlights: [
        'Graduated with honors',
        'Research Assistant in AI Lab',
        'Published 3 conference papers',
        'Member of Graduate Student Council'
      ]
    },
    {
      degree: 'Bachelors in Civil Engineering',
      institution: 'B.M.S College of Engineering',
      location: 'Bengaluru, India',
      period: '2016 - 2020 ',
      gpa: '8.56/10 (CGPA)',
      thesis: 'N/A',
      highlights: [
        'Graduated magna cum laude',
        'Dean\'s List all semesters',
        'President of Computer Science Club',
        'Internship at Google'
      ]
    }
  ];

  const workExperience = [
    {
      position: 'Senior Engineer',
      company: 'Tata Consulting Engineers Limited',
      location: 'Vadodara, India',
      period: 'April 2022 - August 2023 ',
      description: 'Leading cutting-edge research in machine learning and AI applications.',
      achievements: [
        'Led 5 major research projects with $2.5M funding',
        'Published 15 peer-reviewed papers',
        'Mentored 12 graduate students',
        'Established 8 industry partnerships'
      ]
    }
  ];

  const certifications = [
    {
      name: 'Certified Data Scientist',
      issuer: 'Data Science Association',
      year: '2022',
      description: 'Advanced certification in data science methodologies'
    },
    {
      name: 'Machine Learning Specialist',
      issuer: 'AI Institute',
      year: '2021',
      description: 'Specialized certification in machine learning algorithms'
    },
    {
      name: 'Project Management Professional',
      issuer: 'Project Management Institute',
      year: '2020',
      description: 'Professional certification in project management'
    }
  ];

  return (
    <section className="education section" id="education">
      <div className="container">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <h2 className="section-title">Education & Experience</h2>
          <p className="section-subtitle">
            My academic journey and professional experience that shaped my research career
          </p>
        </motion.div>

        <motion.div 
          className="education-section"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          viewport={{ once: true }}
        >
          <h3>Education</h3>
          <div className="education-timeline">
            {education.map((edu, index) => (
              <motion.div
                key={index}
                className="education-item"
                initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
                whileInView={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.6, delay: index * 0.2 }}
                viewport={{ once: true }}
              >
                <div className="education-card card">
                  <div className="education-header">
                    <div className="degree-info">
                      <h4>{edu.degree}</h4>
                      <div className="institution">
                        <GraduationCap size={16} />
                        <span>{edu.institution}</span>
                      </div>
                    </div>
                    <div className="education-meta">
                      <div className="meta-item">
                        <MapPin size={14} />
                        <span>{edu.location}</span>
                      </div>
                      <div className="meta-item">
                        <Calendar size={14} />
                        <span>{edu.period}</span>
                      </div>
                      <div className="meta-item">
                        <Award size={14} />
                        <span>GPA: {edu.gpa}</span>
                      </div>
                    </div>
                  </div>
                  
                  {edu.thesis !== 'N/A' && (
                    <div className="thesis">
                      <strong>Thesis:</strong> {edu.thesis}
                    </div>
                  )}
                  
                  <div className="highlights">
                    <h5>Key Highlights:</h5>
                    <ul>
                      {edu.highlights.map((highlight, highlightIndex) => (
                        <li key={highlightIndex}>{highlight}</li>
                      ))}
                    </ul>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        </motion.div>

        <motion.div 
          className="experience-section"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.4 }}
          viewport={{ once: true }}
        >
          <h3>Work Experience</h3>
          <div className="experience-timeline">
            {workExperience.map((exp, index) => (
              <motion.div
                key={index}
                className="experience-item"
                initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
                whileInView={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.6, delay: index * 0.2 }}
                viewport={{ once: true }}
              >
                <div className="experience-card card">
                  <div className="experience-header">
                    <div className="position-info">
                      <h4>{exp.position}</h4>
                      <div className="company">
                        <Building size={16} />
                        <span>{exp.company}</span>
                      </div>
                    </div>
                    <div className="experience-meta">
                      <div className="meta-item">
                        <MapPin size={14} />
                        <span>{exp.location}</span>
                      </div>
                      <div className="meta-item">
                        <Calendar size={14} />
                        <span>{exp.period}</span>
                      </div>
                    </div>
                  </div>
                  
                  <p className="experience-description">{exp.description}</p>
                  
                  <div className="achievements">
                    <h5>Key Achievements:</h5>
                    <ul>
                      {exp.achievements.map((achievement, achievementIndex) => (
                        <li key={achievementIndex}>{achievement}</li>
                      ))}
                    </ul>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        </motion.div>

        <motion.div 
          className="certifications-section"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.6 }}
          viewport={{ once: true }}
        >
          <h3>Certifications</h3>
          <div className="certifications-grid">
            {certifications.map((cert, index) => (
              <motion.div
                key={index}
                className="certification-card"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
                whileHover={{ y: -5 }}
              >
                <div className="certification-icon">
                  <Award size={24} />
                </div>
                <h4>{cert.name}</h4>
                <p className="certification-issuer">{cert.issuer}</p>
                <p className="certification-year">{cert.year}</p>
                <p className="certification-description">{cert.description}</p>
              </motion.div>
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Education;
