import React from 'react';
import { motion } from 'framer-motion';
import { Download, FileText, Award, Users, Target, Clock } from 'lucide-react';
import './Resume.css';

const Resume = () => {
  const resumeHighlights = [
    {
      icon: <Award size={24} />,
      title: '15+ Years Experience',
      description: 'Extensive experience in research and academia'
    },
    {
      icon: <Users size={24} />,
      title: '50+ Publications',
      description: 'Peer-reviewed papers in top-tier journals'
    },
    {
      icon: <Target size={24} />,
      title: '10+ Research Projects',
      description: 'Leading innovative research initiatives'
    },
    {
      icon: <Clock size={24} />,
      title: '5+ Awards',
      description: 'Recognition for outstanding contributions'
    }
  ];

  const keySkills = {
    'Technical Skills': [
      'Machine Learning', 'Artificial Intelligence', 'Data Science',
      'Python', 'R', 'MATLAB', 'Statistical Analysis', 'Deep Learning'
    ],
    'Research Skills': [
      'Research Design', 'Data Collection', 'Statistical Modeling',
      'Grant Writing', 'Academic Writing', 'Peer Review'
    ],
    'Leadership Skills': [
      'Team Management', 'Project Leadership', 'Mentorship',
      'Collaboration', 'Strategic Planning', 'Communication'
    ]
  };

  const handleDownload = () => {
    // In a real application, this would download the actual resume file
    const link = document.createElement('a');
    link.href = '#';
    link.download = 'Dr_YourName_Resume.pdf';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <section className="resume section" id="resume">
      <div className="container">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <h2 className="section-title">Resume</h2>
          <p className="section-subtitle">
            A comprehensive overview of my professional experience, skills, and achievements
          </p>
        </motion.div>

        <motion.div 
          className="resume-overview"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          viewport={{ once: true }}
        >
          <div className="resume-card card">
            <div className="resume-header">
              <div className="resume-icon">
                <FileText size={48} />
              </div>
              <div className="resume-info">
                <h3>Shashank Hegde - Professional Resume</h3>
                <p>Senior Research Scientist & Academic Professional</p>
                <div className="resume-meta">
                  <span>Last Updated: December 2023</span>
                  <span>Format: PDF</span>
                </div>
              </div>
            </div>
            
            <div className="resume-actions">
              <button className="btn btn-primary" onClick={handleDownload}>
                <Download size={18} />
                Download Resume
              </button>
              <p className="resume-note">
                Click to download a comprehensive PDF version of my resume
              </p>
            </div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Resume;
