import React, { useRef, useMemo } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { Points, PointMaterial } from '@react-three/drei';
import * as THREE from 'three';

function FluidParticles({ count = 2000 }) {
  const mesh = useRef();
  const positions = useMemo(() => {
    const positions = new Float32Array(count * 3);
    for (let i = 0; i < count; i++) {
      positions[i * 3] = (Math.random() - 0.5) * 20; // x
      positions[i * 3 + 1] = (Math.random() - 0.5) * 20; // y
      positions[i * 3 + 2] = (Math.random() - 0.5) * 20; // z
    }
    return positions;
  }, [count]);

  const velocities = useMemo(() => {
    const velocities = new Float32Array(count * 3);
    for (let i = 0; i < count; i++) {
      velocities[i * 3] = (Math.random() - 0.5) * 0.02; // vx
      velocities[i * 3 + 1] = (Math.random() - 0.5) * 0.02; // vy
      velocities[i * 3 + 2] = (Math.random() - 0.5) * 0.02; // vz
    }
    return velocities;
  }, [count]);

  // CFD-inspired velocity field function
  const getVelocityField = (x, y, z, time) => {
    // Simulate a vortex flow field
    const r = Math.sqrt(x * x + y * y);
    const theta = Math.atan2(y, x);
    
    // Vortex strength
    const vortexStrength = 0.5;
    
    // Tangential velocity (vortex flow)
    const vt = vortexStrength / (r + 0.1);
    
    // Radial velocity (slight inward flow)
    const vr = -0.01 * r;
    
    // Convert to Cartesian coordinates
    const vx = vr * Math.cos(theta) - vt * Math.sin(theta);
    const vy = vr * Math.sin(theta) + vt * Math.cos(theta);
    const vz = Math.sin(time * 0.5 + x * 0.1) * 0.01;
    
    return { vx, vy, vz };
  };

  useFrame((state) => {
    const time = state.clock.getElapsedTime();
    const positions = mesh.current.geometry.attributes.position.array;
    
    for (let i = 0; i < count; i++) {
      const ix = i * 3;
      const iy = i * 3 + 1;
      const iz = i * 3 + 2;
      
      const x = positions[ix];
      const y = positions[iy];
      const z = positions[iz];
      
      // Get velocity from CFD-inspired field
      const velocity = getVelocityField(x, y, z, time);
      
      // Update positions with velocity field
      positions[ix] += velocity.vx;
      positions[iy] += velocity.vy;
      positions[iz] += velocity.vz;
      
      // Add some turbulence
      positions[ix] += Math.sin(time * 0.5 + i * 0.1) * 0.005;
      positions[iy] += Math.cos(time * 0.3 + i * 0.1) * 0.005;
      positions[iz] += Math.sin(time * 0.7 + i * 0.1) * 0.005;
      
      // Boundary conditions - wrap around
      if (positions[ix] > 10) positions[ix] = -10;
      if (positions[ix] < -10) positions[ix] = 10;
      if (positions[iy] > 10) positions[iy] = -10;
      if (positions[iy] < -10) positions[iy] = 10;
      if (positions[iz] > 10) positions[iz] = -10;
      if (positions[iz] < -10) positions[iz] = 10;
    }
    
    mesh.current.geometry.attributes.position.needsUpdate = true;
  });

  return (
    <Points ref={mesh}>
      <bufferGeometry>
        <bufferAttribute
          attach="attributes-position"
          count={count}
          array={positions}
          itemSize={3}
        />
      </bufferGeometry>
      <PointMaterial
        transparent
        color="#4a90e2"
        size={0.1}
        sizeAttenuation={true}
        depthWrite={false}
        blending={THREE.AdditiveBlending}
      />
    </Points>
  );
}

function VortexField() {
  const mesh = useRef();
  
  useFrame((state) => {
    const time = state.clock.getElapsedTime();
    mesh.current.rotation.x = Math.sin(time * 0.1) * 0.1;
    mesh.current.rotation.y = Math.cos(time * 0.1) * 0.1;
  });

  return (
    <mesh ref={mesh} position={[0, 0, -5]}>
      <torusGeometry args={[8, 2, 16, 100]} />
      <meshBasicMaterial 
        color="#1e3a8a" 
        transparent 
        opacity={0.1} 
        wireframe 
      />
    </mesh>
  );
}

function PressureGradient() {
  const mesh = useRef();
  
  useFrame((state) => {
    const time = state.clock.getElapsedTime();
    mesh.current.rotation.z = time * 0.05;
    mesh.current.material.opacity = 0.05 + Math.sin(time * 0.5) * 0.02;
  });

  return (
    <mesh ref={mesh} position={[0, 0, -3]}>
      <sphereGeometry args={[12, 32, 32]} />
      <meshBasicMaterial 
        color="#3b82f6" 
        transparent 
        opacity={0.05} 
        wireframe 
      />
    </mesh>
  );
}

function Streamlines() {
  const mesh = useRef();
  
  useFrame((state) => {
    const time = state.clock.getElapsedTime();
    mesh.current.rotation.y = time * 0.02;
  });

  return (
    <group ref={mesh}>
      {[...Array(8)].map((_, i) => (
        <mesh key={i} position={[0, 0, -2]}>
          <cylinderGeometry args={[0.02, 0.02, 15, 8]} />
          <meshBasicMaterial 
            color="#60a5fa" 
            transparent 
            opacity={0.1} 
            wireframe 
          />
        </mesh>
      ))}
    </group>
  );
}

function FluidBackground() {
  return (
    <div className="fluid-background">
      <Canvas
        camera={{ position: [0, 0, 15], fov: 75 }}
        style={{ position: 'fixed', top: 0, left: 0, zIndex: -1 }}
      >
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} intensity={1} />
        <FluidParticles count={3000} />
        <VortexField />
      </Canvas>
    </div>
  );
}

export default FluidBackground;
