import React from 'react';
import { motion } from 'framer-motion';
import { Building, MapPin, Calendar, Users, Award, Target } from 'lucide-react';
import './CurrentPosition.css';

const CurrentPosition = () => {
  const currentRole = {
    title: 'Senior Research Scientist',
    institution: 'University of Technology',
    department: 'Department of Computer Science',
    location: 'San Francisco, CA',
    startDate: 'January 2022',
    status: 'Current',
    description: 'Leading cutting-edge research initiatives in machine learning and artificial intelligence, with focus on developing innovative solutions for real-world applications.'
  };

  const responsibilities = [
    {
      icon: <Target size={20} />,
      title: 'Research Leadership',
      description: 'Leading research teams and projects in advanced machine learning applications'
    },
    {
      icon: <Users size={20} />,
      title: 'Mentorship',
      description: 'Mentoring graduate students and postdoctoral researchers in their academic and research pursuits'
    },
    {
      icon: <Award size={20} />,
      title: 'Grant Management',
      description: 'Managing research grants and funding totaling over $2M in active projects'
    },
    {
      icon: <Building size={20} />,
      title: 'Collaboration',
      description: 'Collaborating with industry partners and other academic institutions on joint research initiatives'
    }
  ];

  const achievements = [
    'Led 5 major research projects with total funding of $2.5M',
    'Published 15 peer-reviewed papers in top-tier journals',
    'Mentored 12 graduate students and 3 postdoctoral researchers',
    'Received 3 prestigious research awards and recognitions',
    'Established partnerships with 8 industry leaders',
    'Organized 2 international conferences and workshops'
  ];

  const skills = [
    'Machine Learning', 'Artificial Intelligence', 'Data Science',
    'Research Methodology', 'Project Management', 'Team Leadership',
    'Grant Writing', 'Academic Writing', 'Public Speaking',
    'Statistical Analysis', 'Programming', 'Collaboration'
  ];

  return (
    <section className="current-position section" id="current-position">
      <div className="container">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <h2 className="section-title">Current Position</h2>
          <p className="section-subtitle">
            My current role and responsibilities in advancing research and academic excellence
          </p>
        </motion.div>

        <motion.div 
          className="position-overview"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          viewport={{ once: true }}
        >
          <div className="position-card card">
            <div className="position-header">
              <div className="position-title">
                <h3>{currentRole.title}</h3>
                <span className="status-badge">{currentRole.status}</span>
              </div>
              <div className="institution-info">
                <div className="institution-name">
                  <Building size={20} />
                  <span>{currentRole.institution}</span>
                </div>
                <div className="department">{currentRole.department}</div>
              </div>
            </div>

            <div className="position-details">
              <div className="detail-row">
                <div className="detail-item">
                  <MapPin size={16} />
                  <span>{currentRole.location}</span>
                </div>
                <div className="detail-item">
                  <Calendar size={16} />
                  <span>{currentRole.startDate} - Present</span>
                </div>
              </div>
            </div>

            <p className="position-description">{currentRole.description}</p>
          </div>
        </motion.div>

        <motion.div 
          className="responsibilities-section"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.4 }}
          viewport={{ once: true }}
        >
          <h3>Key Responsibilities</h3>
          <div className="responsibilities-grid">
            {responsibilities.map((responsibility, index) => (
              <motion.div
                key={index}
                className="responsibility-card"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
                whileHover={{ y: -5 }}
              >
                <div className="responsibility-icon">{responsibility.icon}</div>
                <h4>{responsibility.title}</h4>
                <p>{responsibility.description}</p>
              </motion.div>
            ))}
          </div>
        </motion.div>

        <div className="position-content">
          <motion.div 
            className="achievements-section"
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
            viewport={{ once: true }}
          >
            <h3>Key Achievements</h3>
            <ul className="achievements-list">
              {achievements.map((achievement, index) => (
                <motion.li
                  key={index}
                  initial={{ opacity: 0, x: -20 }}
                  whileInView={{ opacity: 1, x: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  viewport={{ once: true }}
                >
                  {achievement}
                </motion.li>
              ))}
            </ul>
          </motion.div>

          <motion.div 
            className="skills-section"
            initial={{ opacity: 0, x: 50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8, delay: 0.8 }}
            viewport={{ once: true }}
          >
            <h3>Core Skills</h3>
            <div className="skills-grid">
              {skills.map((skill, index) => (
                <motion.span
                  key={index}
                  className="skill-tag"
                  initial={{ opacity: 0, scale: 0.8 }}
                  whileInView={{ opacity: 1, scale: 1 }}
                  transition={{ duration: 0.6, delay: index * 0.05 }}
                  viewport={{ once: true }}
                >
                  {skill}
                </motion.span>
              ))}
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default CurrentPosition;
